import java.lang.*;

public class Problem8{
    public static void main(String args[]){
	char[] temp = new char[5];
	String bigString = initializeString();
	int tempProduct = 1;
	int maxProduct = 1;

	for( int i = 0 ; i < 996 ; i++){
	    bigString.getChars( i , i + 5, temp, 0);

	    tempProduct = 1;
	    for( int j = 0 ; j < 5 ; j++){
		tempProduct *= Character.digit( temp[j], 10);
	    }

	    maxProduct = maxProduct >= tempProduct ? maxProduct : tempProduct;

//	    possibleString = Integer.toString( temp);

//	    if( existsInBigString( possibleString))
//		System.out.println( possibleString + " found");
	}

	System.out.println(maxProduct);
    }

    public static boolean existsInBigString( String possibleString){
	String bigString = initializeString();
	boolean toReturn = false;

	for(int i = 0 ; i < bigString.length() ; i++){

	    if( i + possibleString.length() > bigString.length())
		break; //or i = 1000

	    if( possibleString.contentEquals( bigString.subSequence( i , i + possibleString.length())) == true){
//		System.out.println("found " + possibleString + " at " + i);
		toReturn = true;
	    }
	}

	return toReturn;
    }

    public static String initializeString(){
	String toReturn = "";

	return toReturn;
    }



}
